```cpp
#include &lt;bits/stdc++.h&gt;
#define reg register
#define inl inline
#define M 1000000
#define N 1000
using namespace std;
struct edge {
	int u,v,w;
}	e[M|1];
int T,t,n,k,s,c,p[N|1];
inl bool cmp (reg edge l,reg edge r) { return l.w&lt;r.w; }
inl int getp (reg int x) {
	if (p[x]==x)	return x;
	return			p[x]=getp (p[x]);
}
inl void merge (reg int l,reg int r) {
	l=getp (l); r=getp (r);
	if (l!=r)	p[l]=r;
}
int main () {
	for (scanf ("%d",&amp;T);T;T--) {
		s=k=0;
		memset (p,0,sizeof(p));
		memset (e,0,sizeof(e));
		scanf ("%d",&amp;n);
		for (reg int i=1;i&lt;=n;i++)
			p[i]=i;
		for (reg int i=1;i&lt;=n;i++)
			for (reg int j=1;j&lt;=n;j++) {
				scanf ("%d",&amp;t);
				if (i&lt;j) {
					e[++k].w=t;
					e[k].u=i,e[k].v=j;
				}
			}
		sort (e+1,e+1+k,cmp);
		for (reg int i=1;i&lt;=k;i++)
			if (getp(e[i].u)!=getp(e[i].v)) {
				merge (e[i].u,e[i].v);	s=max (s,e[i].w);
				if (c+1==n)				break;
			}
		printf ("%d\n",s);
	}
	return 0;
}
