#include &lt;bits/stdc++.h&gt;
using namespace std;
int len(int k)
{
  	if (k==-1) return 0; 
  	int x=len(k-1);
  	return x+k+3+x;
}
char solve(int n,int k)
{
  if (n&gt;len(k)) return solve(n,k+1);
  if (n&lt;=len(k-1)) return solve(n,k-1);
  n=n-len(k-1);
  if (n&lt;=k+3) 
    return (n==1)?'m':'o';
  n=n-(k+3);
  return solve(n,k-1); 
}

int main(void)
{
  int n;
  cin&gt;&gt;n;
  cout&lt;&lt;solve(n,0)&lt;&lt;endl;
  return 0;
}
