```cpp
#include&lt;bits/stdc++.h&gt;
using namespace std;
const int maxn=10000+10;
int n,heap[maxn],size=0;
void up(int p)
{
  while(p&gt;1)
  {
    if(heap[p]&lt;heap[p/2])
    {
      swap(heap[p],heap[p/2]);
      p/=2;
    }
    else break;
  }
}
void insert(int val)
{
  heap[++size]=val;
  up(size);
}
void down(int p)
{
  int s=p*2;
  while(s&lt;=size)
  {
    if(s&lt;size&amp;&amp;heap[s+1]&lt;heap[s]) s++; 
    if(heap[s]&lt;heap[p])
    {
      swap(heap[s],heap[p]);
      p=s; s=p*2;
    }
    else break;
  }
}
void extract()
{
  heap[1]=heap[size--];
  down(1);
}
int gettop()
{
  return heap[1];
}
int main()
{
  cin&gt;&gt;n;
  for(int i=1; i&lt;=n; i++)
  {
    int a; cin&gt;&gt;a;
    insert(a);
  }
  long long ans=0; 
  while(size&gt;=2) 
  {
    int top1=gettop(); 
    extract();
    int top2=gettop();
    extract();
    ans+=(top1+top2);
    insert(top1+top2);
  }
  cout&lt;&lt;ans&lt;&lt;endl;
  return 0;
}
