#include &lt;iostream&gt;
using namespace std;

class Permutation
{
public:
    void Perm(int a[],int k,int n);               
    void PermOfThree(int a[]);                   
private:
    int a[];
};


void Permutation::Perm(int a[],int k, int n)
{
    if(k&lt;n-1)
    {
        for(int i=k;i&lt;n;i++){                 
            int t=a[k];
            a[k]=a[i];
            a[i]=t;
            Perm(a,k+1,n);                    
            t=a[k];
            a[k]=a[i];
            a[i]=t;
        }

    }
    else{
        PermOfThree(a);
    }
}
void Permutation::PermOfThree(int a[])
       {
           double one = a[0]*100 + a[1]*10 + a[2];
           double two = a[3]*100 + a[4]*10 + a[5];
           double three = a[6]*100 + a[7]*10 + a[8];
           if( (two/one) == 2 &amp;&amp; (three/one) == 3 )
           {
              cout&lt;&lt;one&lt;&lt;" "&lt;&lt;two&lt;&lt;" "&lt;&lt;three&lt;&lt;endl;
           }
       }
int main()
{
    int a[]={1,2,3,4,5,6,7,8,9};
    cout&lt;&lt;"4"&lt;&lt;endl; 
    Permutation p;
    p.Perm(a,0,9);
    return 0;
}
