#include&lt;bits/stdc++.h&gt;
using namespace std;
string a,b;
long long er[30],san[30],Er,San;
int possible_er[10010],possible_san[10010];
long c2,c3;
int power(int a,int b,int c)
{
	int x=1;
	for(int i=1;i&lt;=c;i++)
		x*=b;
	return x*a;
}
int main()
{
	cin&gt;&gt;a&gt;&gt;b;
	for(int i=0;i&lt;a.size();i++)
		er[a.size()-i-1]=a[i]-'0';
	for(int i=0;i&lt;b.size();i++)
		san[b.size()-i-1]=b[i]-'0';
	for(int i=0;i&lt;=a.size();i++)
		Er+=power(er[i],2,i);
	for(int i=0;i&lt;=b.size();i++)
		San+=power(san[i],3,i);

	for(int i=0;i&lt;a.size()-1;i++)
	{
		if(er[i]==0) 
			possible_er[c2]=Er+power(1,2,i),c2++;
		if(er[i]==1)
			possible_er[c2]=Er-power(1,2,i),c2++;
	}
	c2--;
	for(int i=0;i&lt;b.size();i++)
	{
		if(san[i]==0)
		{
			possible_san[c3]=San+power(1,3,i),c3++;
			possible_san[c3]=San+power(2,3,i),c3++;
		}
		if(san[i]==1)
		{
			possible_san[c3]=San+power(-1,3,i),c3++;
			possible_san[c3]=San+power(1,3,i),c3++;
		}
		if(san[i]==2)
		{
			possible_san[c3]=San+power(-1,3,i),c3++;
			possible_san[c3]=San+power(-2,3,i),c3++;
		}
	}	
	c3--;

	for(int i=0;i&lt;=c2;i++)
		for(int j=0;j&lt;=c3;j++)
			if(possible_er[i]==possible_san[j])
			{
				cout&lt;&lt;possible_er[i]&lt;&lt;endl;
				break;
			}
	getchar();
	return 0;
}
//AC 100分
