#include&lt;bits/stdc++.h&gt;
using namespace std;
int ans,n,a;
void fenjie(int remain,int pre)
{
    if (remain==1) { ++ans; return; }
    for (int i=pre;i*i&lt;=remain;++i)
        if (remain%i==0)
            fenjie(remain/i,i);
    fenjie(1,remain);
}
int main()
{
    cin&gt;&gt;n;
    for(int i=1;i&lt;=n;++i) {
        cin&gt;&gt;a;
        ans=0;
        fenjie(a,2);
        cout&lt;&lt;ans&lt;&lt;endl;
    }
    return 0;
}
