void up(int p)
{
  while(p>1)
  {
    if(heap[p]<heap[p/2])
    {
      swap(heap[p],heap[p/2]);
      p/=2;
    }
    else break;
  }
}
void insert(int val)
{
  heap[++size]=val;
  up(size);
}
void down(int p)
{
  int s=p*2;
  while(s<=size)
  {
    if(s<size&&heap[s+1]<heap[s]) s++; 
    if(heap[s]<heap[p])
    {
      swap(heap[s],heap[p]);
      p=s; s=p*2;
    }
    else break;
  }
}
void extract()
{
  heap[1]=heap[size--];
  down(1);
}
int gettop()
{
